const API_URL = 'https://strangers-things.herokuapp.com/api/2206-FTB-ET-WEB-FT-B'


const postUser = async (userName, passWord) => {
    const newUser = await fetch(`${API_URL}/users/register`, {
        method: "POST",
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify({
            user: {
                username: userName,
                password: passWord
            }
        })
    }).then(response => response.json())
    .then(result => {
        console.log(result);
    })
    .catch(console.error)
}


const Register = () => {

    return (
        <div className="register">
            <h1>REGISTER</h1>
            <form>
                <label>Username:</label>
                <input id='username' type='text' placeholder="username" required></input>
                <br />
                <label>Password:</label>
                <input id='password1' type='password' placeholder="password" required></input>
                <br />
                <label>Confirm Password:</label>
                <input id='password2' type='password' placeholder="password" required></input>
                <button type="submit">Submit</button>
            </form>
        </div>
    )
}

export default Register;
