const API_URL = 'https://strangers-things.herokuapp.com/api/2206-FTB-ET-WEB-FT-B'

const ViewPost = (props) => {
    const { postIndex } = props;

    const fetchThisPost = async () => {
        await fetch(`${API_URL}/posts`)
        .then(response => response.json())
            .then(result => {
                console.log(result.data.posts[postIndex])
                console.log(result.data.posts[0].title)
                return result.data.posts[postIndex]
            })
            .catch(console.error);
    }

    const thisPost = fetchThisPost();
    console.log('title: ', thisPost.title)

    return (
        <div id="post">
            <h1>HI</h1>
            <h1>{thisPost.title}</h1>
        </div>
    )
}

export default ViewPost;
