import { useEffect } from "react";
import { useHistory } from "react-router-dom";

const API_URL = 'https://strangers-things.herokuapp.com/api/2206-FTB-ET-WEB-FT-B'

const Posts = (props) => {
    const { userName, setAlertMessage, setPostID, setPostIndex, setPostArray, postArray } = props;
    const history = useHistory();


    useEffect(() => {
        const initialPosts = async () => {
            const posts = await fetchPosts();
            renderPosts(posts);
        }

        initialPosts();        
    }, postArray)

    return (
        <div className="posts">
            <h1>{ userName ? `Welcome back ${userName}!!` : `Welcome! Log in to get started!`}</h1>
            <div id="post-stuff">
                <div id="search-posts">
                    <input type='text' placeholder="Search Posts"></input>
                    <button>Search</button>
                </div>
                <div id="add-post">
                    <button onClick={() => {
                        if (userName) {
                            history.push('/newPost');
                        } else {
                            setAlertMessage("You must be logged in in order to make a post.");
                            history.push('/login')
                        }
                    }}>Create New Post</button>
                </div>
            </div>
            <div id='posts-container'></div>
        </div>
    )
}

export default Posts;
