import Register from "./register";
import { BrowserRouter, Route, Link, Redirect } from 'react-router-dom';

const API_URL = 'https://strangers-things.herokuapp.com/api/2206-FTB-ET-WEB-FT-B'

const Login = (props) => {
    const { setAlertMessage, userName, setUserName, setLocalStorage } = props;

    const submitLogin = async (event) => {
        const password1Input = document.getElementById('login-password').value;
        event.preventDefault();

        let userData = {
            username: userName,
            password: password1Input
        };

        await getUserToken(userData)
    }

    const getUserToken = async (userData) => {
        const user = await fetch(`${API_URL}/users/login`, {
            method: "POST",
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                user: {
                    username: userData.username,
                    password: userData.password
                }
            })
        }).then(response => response.json())
        .then(result => {
            console.log(result)
            if (!result.error) {
                setAlertMessage(result.data.message);
                setLocalStorage(result.data.token);
            } else {
                setAlertMessage(result.error.message);
            }
        })
        .catch(console.error)
    }

    return (
        <BrowserRouter>
            <div className="login">
                <h1>LOGIN</h1>
                <div id='login-container'>
                    <form id='login-form'>
                        <label>Username:</label>
                        <input id='login-username' type='text' placeholder="Enter Username" required onChange={(event) => {setUserName(event.target.value)}}></input>
                        <br />
                        <label>Password:</label>
                        <input id='login-password' type='password' placeholder="Enter Password" required></input>
                        <br />
                        <button type="submit" onClick={submitLogin}>Submit</button>
                    </form>
                        <a href="">Don't have a log in? Click here to register!</a>
                </div>
            </div>
        </BrowserRouter>
    )
}

export default Login;
