import React, { useEffect, useState, useRef } from 'react';
import ReactDOM from 'react-dom';
import { BrowserRouter, Route, Link } from 'react-router-dom';
import Register from './register';
import Posts from './posts';
import Login from './login';
import CustomAlert, { showAlert } from './alert';
import Logout from './logout'
import NewPost from './newPost';
import ViewPost from './viewPost';
import Messages from './messages';


const App = () => {
    const [userName, setUserName] = useState('')
    const [alertMessage, setAlertMessage] = useState('');
    const [token, setToken] = useState('');
    const [logText, setLogText] = useState('LOGIN')
    const isMounted = useRef(false);
    const [postIndex, setPostIndex] = useState('');
    const [postID, setPostID] = useState('');
    const [postArray, setPostArray] = useState([])

    useEffect(() => {
     if (isMounted.current) {
         showAlert()
     } else {
         isMounted.current = true;
     }}, [alertMessage]);

    const fetchPosts = async () => {
        try {
            const posts = await fetch(`${API_URL}/posts`);
            const postsResults = await posts.json();
            return postsResults.data.posts;

        } catch (err) {
            console.error('Unable to fetch posts', err);
        }
    }


    return (
        <BrowserRouter>
            <div className='header'>
                <div className='title'>
                    <h1>STRANGER'S THINGS</h1>
                </div>
                <nav className='nav'>
                    <Link to="/" className='nav-link' id='nav-home'>HOME</Link>
                    { userName ? <Link to="/messages" className='nav-link' id='nav-messages'>MESSAGES</Link> : null}
                    <Link to={logText === "LOGIN" ? "/login" : "/logout"} className='nav-link' id='nav-login'>{logText}</Link>
                </nav>
            </div>
            <div className='main'>
                <Route exact path="/">
                    <Posts
                        postIndex={postIndex}
                        postID={postID}
                        setPostIndex={setPostIndex}
                        userName={userName}
                        setAlertMessage={setAlertMessage}
                        setPostID={setPostID}
                        setPostArray={setPostArray} />
                </Route>
                <Route exact path="/login">
                    <Login 
                        setUserName={setUserName}
                        setAlertMessage={setAlertMessage}
                        setToken={setToken}
                        logText={logText}
                        setLogText={setLogText} />
                </Route>
                <Route exact path="/register">
                    <Register 
                        setUserName={setUserName}
                        setAlertMessage={setAlertMessage}
                        setToken={setToken}
                        setLogText={setLogText} />
                </Route>
                <Route exact path="/logout">
                    <Logout 
                        setAlertMessage={setAlertMessage}
                        setUserName={setUserName}
                        setToken={setToken}
                        setLogText={setLogText} />
                </Route>
                <Route exact path="/newPost">
                    <NewPost
                        setAlertMessage={setAlertMessage}
                        token={token} />
                </Route>
                <Route exact path="/viewPost">
                    <ViewPost 
                        postIndex={postIndex}
                        postID={postID}
                        userName={userName} 
                        token={token}
                        setAlertMessage={setAlertMessage} />
                </Route>
                <Route exact path="/messages">
                    <Messages
                        token={token}
                        userName={userName}
                        setPostID={setPostID}
                        postID={postID}
                        postArray={postArray}
                        setPostIndex={setPostIndex} />
                </Route>
            </div>
            <div className='alert'>
                <CustomAlert alertMessage={alertMessage} />
            </div>
        </BrowserRouter>
    )
}

const appElement = document.getElementById('app');

ReactDOM.render(<App />, appElement);
