const API_URL = 'https://strangers-things.herokuapp.com/api/2206-FTB-ET-WEB-FT-B'

const Messages = (props) => {
    const { token, userName } = props;

    const getMessages = async () => {
        try {
            const messages = await fetch(`${API_URL}/users/me`, {
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },})
            const messagesResults = await messages.json();
            return messagesResults.data.messages;
        } catch (err) {
            console.error('Unable to fetch messages', err);
        }
    }

    const renderMessages = (messageList) => {
        console.log(messageList)

        let messageContainer = document.getElementById('message-container')

        if (!messageList) {
            messageContainer.innerHTML = "No messages to display."
        }

        let messageContainerHTML = '';
        for (let i = 0; i < messageList.length; i++) {
            const message = messageList[i];

            let messageHTML = `
            <div class='single-message'>
                <div class='sm-author'>
                    <h3>From: ${message.fromUser}</h3>
                </div>
                <div class='sm-content>
                    <p>${message.content}</p>
                </div>
                <div class='sm-post'>
                    <p>${message.post.title}
                </div>
            </div>`;

            messageContainerHTML += messageHTML;
        }
        messageContainer.innerHTML = messageContainerHTML;

    }

    const initialMessages = async () => {
        const messages = await getMessages();
        renderMessages(messages)
    }

    initialMessages();

    return (
        <div id="message-container">

        </div>
    )
}

export default Messages;
