import { showAlert, removeAlertTimer } from "./alert";

const API_URL = 'https://strangers-things.herokuapp.com/api/2206-FTB-ET-WEB-FT-B'

const postUser = async (userData) => {
    const newUser = await fetch(`${API_URL}/users/register`, {
        method: "POST",
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify({
            user: {
                username: userData.username,
                password: userData.password
            }
        })
    }).then(response => response.json())
    .then(result => {
        console.log(result);
    })
    .catch(console.error)
}

const submitRegistration = async (event) => {
    const form = document.getElementById('register-form')
    const usernameInput = document.getElementById('register-username').value;
    const password1Input = document.getElementById('register-password1').value;
    const password2Input = document.getElementById('register-password2').value;
    let userData = {};
    event.preventDefault();

    if (password1Input === password2Input) {
        userData = {
            username: usernameInput,
            password: password1Input
        };
    } else {
        alert('Passwords do not match, try again!')
    }

    await postUser(userData);
    showAlert();
    removeAlertTimer();
}

const Register = () => {
    return (
        <div className="register">
            <h1>REGISTER</h1>
            <div id="form-container">
                <form id='register-form'>
                    <label>Username:</label>
                    <input id='register-username' type='text' placeholder="Create a Username" required></input>
                    <br />
                    <label>Password:</label>
                    <input id='register-password1' type='password' placeholder="Create a Password" required></input>
                    <br />
                    <label>Confirm Password:</label>
                    <input id='register-password2' type='password' placeholder="Confirm Password" required></input>
                    <br />
                    <button type="submit" onClick={submitRegistration}>Submit</button>
                </form>
            </div>
        </div>
    )
}

export default Register;
