const API_URL = 'https://strangers-things.herokuapp.com/api/2206-FTB-ET-WEB-FT-B'

const ViewPost = (props) => {
    const { postID, token, postIndex } = props;
    let post = {};

    const fetchThisPost = async () => {
        fetch(`${API_URL}/posts`, {
            method: "GET",
          }).then(response => response.json())
            .then(result => {
                console.log(result.data.posts[postIndex])
                post += (result.data.posts[postIndex])
            })
            .catch(console.error);
    }

    fetchThisPost();
    useEffect(()=> {
        console.log(post)
    })
    console.log(post)

    return (
        <div id="post-container">HI</div>
    )
}

