import { useEffect } from "react";

const API_URL = 'https://strangers-things.herokuapp.com/api/2206-FTB-ET-WEB-FT-B'

const ViewPost = (props) => {
    const { postID, token, postIndex } = props;

    const fetchThisPost = async () => {
        fetch(`${API_URL}/posts`, {
            method: "GET",
          }).then(response => response.json())
            .then(result => {
                console.log(result.data[postIndex])
                const postResult = result[postIndex]
                return postResult
            })
            .catch(console.error);
    }

    const post = fetchThisPost();

    return (
        <div id="post-container">
            <h1>HI</h1>
            <h1>{post.description}</h1>
        </div>
    )
}

export default ViewPost;
