import React, { useEffect, useState } from 'react';
import ReactDOM from 'react-dom';
import { BrowserRouter, Route, Link, useHistory } from 'react-router-dom';
import Home from './home';
import Register from './register';
import Posts from './posts';
import Login from './login';
import CustomAlert, { showAlert } from './alert';


const App = () => {
    const [userName, setUserName] = useState('')
    const [alertMessage, setAlertMessage] = useState("Welcome to Stranger's Things, make an account to begin!");
    const [localStorage, setLocalStorage] = useState('');
    const [logText, setLogText] = useState('LOGIN')
    const history = useHistory();

    useEffect(showAlert, [alertMessage]);
    useEffect(() => {
        console.log(localStorage)    
    }, [localStorage])

    return (
        <BrowserRouter>
            <div className='header'>
                <div className='title'>
                    <h1>STRANGER'S THINGS</h1>
                </div>
                <nav className='nav'>
                    <Link to="/" className='nav-link' id='nav-home'>HOME</Link>
                    <Link to="/posts" className='nav-link' id='nav-posts'>POSTS</Link>
                    <Link to={logText === "LOGIN" ? "/login" : "/home"} className='nav-link' id='nav-login'>{logText}</Link>
                </nav>
            </div>
            <div className='main'>
                <Route exact path="/">
                    <Home />
                </Route>
                <Route exact path="/posts">
                    <Posts />
                </Route>
                <Route exact path="/login">
                    <Login userName={userName} setUserName={setUserName} setAlertMessage={setAlertMessage} setLocalStorage={setLocalStorage} logText={logText} setLogText={setLogText} />
                </Route>
                <Route exact path="/register">
                    <Register userName={userName} setUserName={setUserName} setAlertMessage={setAlertMessage} localStorage={localStorage} setLocalStorage={setLocalStorage}/>
                </Route>
                <Route>
                    <Logout setAlertMessage={setAlertMessage} />
                </Route>
            </div>
            <div className='alert'>
                <CustomAlert alertMessage={alertMessage} />
            </div>
        </BrowserRouter>
    )
}

const appElement = document.getElementById('app');

ReactDOM.render(<App />, appElement);
