const API_URL = 'https://strangers-things.herokuapp.com/api/2206-FTB-ET-WEB-FT-B'

const ViewPost = (props) => {
    const { postID, token, postIndex } = props;

    const fetchThisPost = async () => {
        fetch(`${API_URL}/posts`, {
            method: "GET",
          }).then(response => response.json())
            .then(result => {
                console.log(result.data.posts[postIndex])
                return result.data.posts[postIndex]
            })
            .catch(console.error);
    }

    const renderThisPost = (post) => {
        const postContainer = document.getElementById("post-container")

        let postContainerHTML = ""
        let postHTML = `
            <div class="single-post">
                <div class="post-title">
                    <h2>${post.title}</h2>
                    <div class="send-message">
                        <button data-id=${post._id} class="view-post-button">${post.author.username === userName ? "VIEW MY POST" : "SEND MESSAGE"}</button>
                    </div>
                </div>
                <div class="post-description">
                    <p>${post.description}</p>
                </div>
                <div class="post-price">
                    <p>Price: ${post.price}</p>
                </div>
                <div class="post-location">
                    <p>Location: ${post.location}</p>
                </div>
                <div class="post-username">
                    <p>Seller: ${post.author.username}</p>
                </div>
            </div>
        `;

        postContainerHTML += postHTML

        postContainer.innerHTML = post
    }

    fetchThisPost();

    console.log(post)

    return (
        <div id="post-container"></div>
    )
}

export default ViewPost;
