const API_URL = 'https://strangers-things.herokuapp.com/api/2206-FTB-ET-WEB-FT-B'


const postUser = async (userData) => {
    const newUser = await fetch(`${API_URL}/users/register`, {
        method: "POST",
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify({
            user: {
                username: userData.username,
                password: userData.password
            }
        })
    }).then(response => response.json())
    .then(result => {
        console.log(result);
    })
    .catch(console.error)
}

const formContainer = document.getElementById("form-container")

const renderRegisterForm = () => {
    let formHTML = `
        <form>
            <label>Username:</label>
            <input id='username' type='text' placeholder="username" required></input>
            <br />
            <label>Password:</label>
            <input id='password1' type='password' placeholder="password" required></input>
            <br />
            <label>Confirm Password:</label>
            <input id='password2' type='password' placeholder="password" required></input>
            <button type="submit">Submit</button>
        </form>
    `;

    formContainer.innerHTML = formHTML;

    let form = document.querySelector('#form-container > form')
    form.addEventListener('submit', async (event) => {
        event.preventDefault();

        let userData = {
            username: form.elements.username.value,
            password: form.elements.password.value
        };

        await postUser(userData)
    })


}

renderRegisterForm();

const Register = () => {

    return (
        <div className="register">
            <h1>REGISTER</h1>
            <div id="form-container"></div>
        </div>
    )
}

export default Register;
