import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import { BrowserRouter, Route, Link } from 'react-router-dom';
import Home from './home';
import Register from './register';
import Posts from './posts';
import Login from './login';
import CustomAlert from './alert';


const App = () => {
    const [alertMessage, setAlertMessage] = useState(' ');
    const [localStorage, setLocalStorage] = useState('');

    return (
        <BrowserRouter>
            <div className='header'>
                <div className='title'>
                    <h1>STRANGER'S THINGS</h1>
                </div>
                <nav className='nav'>
                    <Link to="/" className='nav-link' id='nav-home'>HOME</Link>
                    <Link to="/posts" className='nav-link' id='nav-posts'>POSTS</Link>
                    <Link to="/login" className='nav-link' id='nav-login'>LOGIN</Link>
                </nav>
            </div>
            <div className='main'>
                <Route exact path="/">
                    <Home />
                </Route>
                <Route exact path="/posts">
                    <Posts />
                </Route>
                <Route exact path="/login">
                    <Register setAlertMessage={setAlertMessage}/>
                    <Login setAlertMessage={setAlertMessage}/>
                </Route>
            </div>
            <div className='alert'>
                <CustomAlert alertMessage={alertMessage} />
            </div>
        </BrowserRouter>
    )
}

const appElement = document.getElementById('app');

ReactDOM.render(<App />, appElement);
