import { render } from "react-dom";

const API_URL = 'https://strangers-things.herokuapp.com/api/2206-FTB-ET-WEB-FT-B'

const ViewPost = (props) => {
    const { postIndex } = props;
    let post = {
        title: ""
    }

    const fetchThisPost = async () => {
        await fetch(`${API_URL}/posts`)
        .then(response => response.json())
            .then(result => {
                // console.log(result.data.posts[postIndex])
                // console.log(result.data.posts[postIndex].title)
                post.title = result.data.posts[postIndex].title;
                // return result.data.posts[postIndex]
            })
            .catch(console.error);

        function consoleThings() {
            console.log("title: ", post.title)
        }

        await consoleThings();
    }

    const renderPost = (post) => {
        let postDiv = document.getElementById("post-div")

        if (!post) {
            postDiv.innerHTML = "No such post exists."
        }
    }

    const showPost = async () => {
        const post = await fetchThisPost();
        renderPost(post);
    }

    fetchThisPost();
    // console.log(post.title)

    return (
        <div id="post-div">
        </div>
    )
}

export default ViewPost;
